scriptSource.command:
def VERSION = "5.6.0"
def PROJECT = "Dynamic-Ad-Insertion-cm"
def PROJFILE = "/opt/build/scm/scripts/buildServer/properties/projects/$PROJECT/project.properties"
String changelogtemp = """

projectRoot="/opt/cie/jenkins/jobs/DAI-CampaignMgmt-PackageDeploy-$VERSION/workspace"
commonTargetRoot="target"
webappDir="webapps"

build {
    build_args="grails clean -non-interactive,grails refresh-dependencies -no-tests -non-interactive,grails war -no-tests -non-interactive"
}


build_package {

    pre_pkg_cmds {
            pre_pkg_1="mv \$projectRoot/\$commonTargetRoot/Dynamic-Ad-Insertion-cm*.war \$projectRoot/\$commonTargetRoot/Dynamic-Ad-Insertion-cm.war"
        }

    artifacts {
        name {
            cm="Dynamic-Ad-Insertion-cm.war"
            config="config"
            README="README.md"
        }
        location {
            cm="\$projectRoot/\$commonTargetRoot"
            config="\$projectRoot"
            README="\$projectRoot"
        }
    }
}

"""
File scriptName = new File("$PROJFILE")
        scriptName.write("${changelogtemp}")
        println scriptName

hudson.tasks.Shell.command:
#!/bin/bash
export VERSION="5.6.0"
export PROJECT="Dynamic-Ad-Insertion-cm"
export PROJFILE="/opt/build/scm/scripts/buildServer/properties/projects/$PROJECT/project.properties"

cat $PROJFILE

/opt/build/scripts/CreateJenkinsRelease.sh $PROJECT $PROJECT br_$VERSION ${BUILD_NUMBER}
