hudson.tasks.Shell.command:
#!/bin/ksh
EXIST_VERS=`ssh tcserver@dvappdai02 "grep Implementation-Version /opt/tcserver/instances/smsi/webapps/safi-smsi-server*/META-INF/MANIFEST.MF | cut -d : -f 2 | tr -d ' ' | tr -d '\r'"` VERSION=`ssh tcserver@dvappdai01 "grep Implementation-Version /opt/tcserver/instances/smsi/webapps/safi-smsi-server*/META-INF/MANIFEST.MF | cut -d : -f 2 | tr -d ' ' | tr -d '\r'"`
print Gathering build version to be deployed print -------------------------------------- print print Deploying version ${VERSION} print print
cd /opt/checkouts/oss_deployment/scripts
print Beginning App update from existing version ${EXIST_VERS} to latest package version ${VERSION} print ------------------------------------------------------------------------------------------------------------ print ./tmpdeployapp.sh -a safi-smsi-server -t dai-smsi -e scrum1 -i smsi -r ${VERSION}
