scriptSource.command:
def VERSION = "6.1.3"
def PROJECT = "dai_scm"
def PROJFILE = "/opt/build/scm/scripts/buildServer/properties/projects/$PROJECT/project.properties"

String changelogtemp = """
projectRoot="/opt/cie/jenkins/jobs/dai_scm-PackageDeploy-$VERSION/workspace"
commonTargetRoot="target"
webappDir="webapps"

build {

  build_args="mvn clean package"
}

build_package {
        pre_pkg_cmds {
          pre_pkg_1="cd \$projectRoot/target; mkdir liquibase; cp \$projectRoot/liquibase/*.xml liquibase"
          pre_pkg_2="cd \$projectRoot/target; mv dai_scm-$VERSION-SNAPSHOT.war dai_scm.war"
        }
    artifacts {
        name {
            bin="dai_scm.war"
            lb ="liquibase"
            config="config"
        }
        location {
            bin="\$projectRoot/target"
            lb ="\$projectRoot"
            config="\$projectRoot"
        }
    }
}
"""

File scriptName = new File("$PROJFILE")
        scriptName.write("${changelogtemp}")
        println scriptName

hudson.tasks.Shell.command:
#!/bin/bash -x

export ProjFile="/opt/build/scm/scripts/buildServer/properties/projects/dai_scm/project.properties"
echo "ProjFile -> $ProjFile"
export ProjLoc=`cat $ProjFile | awk -F"=" '/projectRoot/ {print $2;exit;}'`
echo "Replacing -> $ProjLoc with $WORKSPACE"
sed -e "s|${ProjLoc}|\"${WORKSPACE}\"|" -i '' $ProjFile

cat $ProjFile

/opt/build/scripts/CreateJenkinsPackage.sh dai_scm dai_scm br_6.1.3 ${BUILD_NUMBER}

