scriptSource.command:
def VERSION = "4.1.0"
def PROJECT = "int-test-support"
def PROJFILE = "/opt/build/scm/scripts/buildServer/properties/projects/$PROJECT/project.properties"
String changelogtemp = """

projectRoot="/opt/cie/jenkins/jobs/int-test-support-PackageDeploy-1.0.0/workspace"
commonTargetRoot="target"
webappDir="webapps"

build {

  build_args="grails clean -non-interactive,grails war -non-interactive"
}

build_package {
    artifacts {
        name {
            bin="int-test-support.war"
            config="config"
            README="README.md"
        }
        location {
            bin="\$projectRoot/\$commonTargetRoot/"
            config="\$projectRoot"
            README="\$projectRoot"
        }
    }
}

"""
File scriptName = new File("$PROJFILE")
        scriptName.write("${changelogtemp}")
        println scriptName

hudson.tasks.Shell.command:
#!/bin/bash
export VERSION="4.1.0" export PROJECT="int-test-support"
export ProjFile="/opt/build/scm/scripts/buildServer/properties/projects/$PROJECT/project.properties"
cat $ProjFile
/opt/build/scripts/CreateJenkinsPackage.sh $PROJECT $PROJECT br_$VERSION ${BUILD_NUMBER}
echo "Re-Setting scala home to scala 2.9.2" cd /opt/tools rm scala ln -s scala-2.9.2 scala
