hudson.tasks.Shell.command:
#!/bin/bash -x

export ProjFile="/opt/build/scm/scripts/buildServer/properties/projects/ops-dce-domain/project.properties"
echo "ProjFile -> $ProjFile"
export ProjLoc=`cat $ProjFile | awk -F"=" '/projectRoot/ {print $2;exit;}'`
echo "Replacing -> $ProjLoc with $WORKSPACE"
sed -e "s|${ProjLoc}|\"${WORKSPACE}\"|" -i '' $ProjFile

/opt/build/scripts/CreateJenkinsRelease.sh ops-dce-domain ops-dce-domain br_4.0.0 ${BUILD_NUMBER}

#/opt/build/scripts/createRelease.sh ops-dce-domain ops-dce-domain br_1.0.0 ${BUILD_NUMBER}
