hudson.tasks.Shell.command:
#!/bin/bash -x

export ProjFile="/opt/build/scm/scripts/buildServer/properties/projects/oss_bar/project.properties"
echo "ProjFile -> $ProjFile"
export ProjLoc=`cat $ProjFile | awk -F"=" '/projectRoot/ {print $2;exit;}'`
echo "Replacing -> $ProjLoc with $WORKSPACE"
sed -e "s|${ProjLoc}|\"${WORKSPACE}\"|" -i '' $ProjFile

/opt/build/scripts/CreateJenkinsRelease.sh oss_bar oss_bar br_3.0.0 ${BUILD_NUMBER}

#/opt/build/scripts/createRelease.sh oss_bar oss_bar br_3.0.0 ${BUILD_NUMBER}
