hudson.tasks.Shell.command:
#!/bin/bash
VERSION=`ssh tcserver@dvappdai01 "grep Implementation-Version /opt/tcserver/instances/smsipub/webapps/smsi-publisher*/META-INF/MANIFEST.MF | cut -d : -f 2 | tr -d ' ' | tr -d '\r'"`
echo "smsi-publisher-${VERSION}" > /var/www/html/scrum1/smsipub.txt echo "smsi-publisher-${VERSION}" > /var/www/html/scrum1/smsipub.txt
